import { useState } from 'react'
import { Container } from 'react-bootstrap'
import Header from './Components/Header'
import Create from './Components/Create'

function App() {
  const [tasks, setTasks] = useState([])

  const addTasks = (task) => {
    setTasks([...tasks, task])
  }

  return (
    <Container>
      <Header />
      <Create addTasks={addTasks} />
    </Container>
  );
}

export default App;
